schema:
{
 "properties": {
  "memberRequests": {
   "description": "Ref to the name of a 'User' resource",
   "items": {
    "properties": {
     "role": {
      "type": "string"
     },
     "user": {
      "description": "LocalObjectReference contains enough information to let you locate the referenced object inside the same namespace.",
      "properties": {
       "name": {
        "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
        "type": "string"
       }
      },
      "type": "object"
     }
    },
    "required": [
     "role",
     "user"
    ],
    "type": "object"
   },
   "type": "array"
  },
  "metadata": {
   "properties": {
    "autoScan": {
     "description": "Whether to scan images automatically when pushing or not",
     "type": "boolean"
    },
    "enableContentTrust": {
     "description": "Whether content trust is enabled or not If it is, users can not pull unsigned images from this project",
     "type": "boolean"
    },
    "preventVul": {
     "description": "Whether to prevent the vulnerable images from running or not.",
     "type": "boolean"
    },
    "public": {
     "description": "Public status of the Project",
     "type": "boolean"
    },
    "reuseSysCVEAllowlist": {
     "description": "Whether this project reuses the system level CVE allowlist as the allowlist of its own. The valid values are \"true\", \"false\". If set to \"true\", the actual allowlist associated with this project, if any, will be ignored.",
     "type": "boolean"
    },
    "severity": {
     "description": "If a vulnerability's severity is higher than the severity defined here, images can't be pulled. Valid values are \"none\", \"low\", \"medium\", \"high\", \"critical\".",
     "type": "string"
    }
   },
   "required": [
    "public"
   ],
   "type": "object"
  },
  "name": {
   "type": "string"
  },
  "parentInstance": {
   "description": "ParentInstance is a LocalObjectReference to the name of the harbor instance the project is created for",
   "properties": {
    "name": {
     "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
     "type": "string"
    }
   },
   "type": "object"
  },
  "proxyCache": {
   "description": "ProxyCache defines an optional reference to a registry resource. The project will be created as a \"Proxy Cache\" project, if specified.",
   "properties": {
    "registry": {
     "description": "LocalObjectReference contains enough information to let you locate the referenced object inside the same namespace.",
     "properties": {
      "name": {
       "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  },
  "storageLimit": {
   "type": "integer"
  }
 },
 "required": [
  "name",
  "parentInstance"
 ],
 "title": "Project",
 "type": "object"
}
