schema:
{
 "description": "RegistrySpec defines the desired state of a Registry.",
 "properties": {
  "credential": {
   "properties": {
    "secretKeyAccessKey": {
     "description": "Key for the \"AccessKey\" field of the secret referenced in SecretRef",
     "type": "string"
    },
    "secretKeyAccessSecret": {
     "description": "Key for the \"AccessSecret\" field of the secret referenced in SecretRef",
     "type": "string"
    },
    "secretRef": {
     "description": "Secret reference to the credentials",
     "properties": {
      "name": {
       "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
       "type": "string"
      }
     },
     "type": "object"
    },
    "type": {
     "description": "Credential type, such as 'basic', 'oauth'.",
     "type": "string"
    }
   },
   "required": [
    "secretKeyAccessKey",
    "secretKeyAccessSecret",
    "secretRef",
    "type"
   ],
   "type": "object"
  },
  "description": {
   "type": "string"
  },
  "insecure": {
   "description": "Whether or not the TLS certificate will be verified when Harbor tries to access the registry",
   "type": "boolean"
  },
  "name": {
   "type": "string"
  },
  "parentInstance": {
   "description": "ParentInstance is a LocalObjectReference to the name of the harbor instance the registry is created for",
   "properties": {
    "name": {
     "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
     "type": "string"
    }
   },
   "type": "object"
  },
  "type": {
   "type": "string"
  },
  "url": {
   "description": "Target URL of the registry",
   "type": "string"
  }
 },
 "required": [
  "name",
  "parentInstance",
  "type",
  "url"
 ],
 "title": "Registry",
 "type": "object"
}
