schema:
{
 "description": "APISpec configures an API.",
 "properties": {
  "pathPrefix": {
   "type": "string"
  },
  "service": {
   "description": "APIService configures the service to exposed on the edge.",
   "properties": {
    "name": {
     "type": "string"
    },
    "openApiSpec": {
     "description": "OpenAPISpec defines the OpenAPI spec of an API.",
     "properties": {
      "path": {
       "type": "string"
      },
      "port": {
       "description": "APIServiceBackendPort is the service port being referenced.",
       "properties": {
        "name": {
         "description": "name is the name of the port on the Service. This must be an IANA_SVC_NAME (following RFC6335). This is a mutually exclusive setting with \"Number\".",
         "type": "string"
        },
        "number": {
         "description": "number is the numerical port number (e.g. 80) on the Service. This is a mutually exclusive setting with \"Name\".",
         "format": "int32",
         "type": "integer"
        }
       },
       "type": "object"
      },
      "protocol": {
       "type": "string"
      },
      "url": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "port": {
     "description": "port of the referenced service. A port name or port number is required for an APIServiceBackendPort.",
     "properties": {
      "name": {
       "description": "name is the name of the port on the Service. This must be an IANA_SVC_NAME (following RFC6335). This is a mutually exclusive setting with \"Number\".",
       "type": "string"
      },
      "number": {
       "description": "number is the numerical port number (e.g. 80) on the Service. This is a mutually exclusive setting with \"Name\".",
       "format": "int32",
       "type": "integer"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "name",
    "port"
   ],
   "type": "object"
  }
 },
 "required": [
  "pathPrefix",
  "service"
 ],
 "title": "API",
 "type": "object"
}
